var sm = new sn_ws.RESTMessageV2("x_avepo_gao.GAO logon", "Post");
var body = {"userName":"ursula.wu@avepointusdev.onmicrosoft.com", "passWord":"demo12!@"};
sm.setRequestBody(global.JSON.stringify(body));
var response = sm.execute();
var token = response.getBody();
workflow.info(token.replace(/\"/g,''))
var serviceRM= new sn_ws.RESTMessageV2("x_avepo_gao.GetServiceRequest", "Default GET");
serviceRM.setStringParameter("access_token", token.replace(/\"/g,''));
serviceRM.setStringParameter("id", current.getValue('serviceid'));
response = serviceRM.execute();
var responseBody = response.getBody();
var request = global.JSON.parse(responseBody).APIRequest;
var requestName = current.getValue('u_string_1');
workflow.info(requestName);
request.RequestSummary = requestName;
request.SiteTitle = requestName;
request.Url.Url = 'sn01';
var sr = new sn_ws.RESTMessageV2("x_avepo_gao.SaveRequest", "Post");
sr.setStringParameter("access_token", token.replace(/\"/g,''));
sr.setRequestBody(global.JSON.stringify(request));
sr.setStringParameter("token", token);
response = sr.execute();

var id = response.getBody();

workflow.info(id)

